const http = require('http');
const fs = require('fs');
const path = require('path');

const PORT = 5000;
const HOST = '0.0.0.0';

// Types MIME
const mimeTypes = {
    '.html': 'text/html',
    '.css': 'text/css',
    '.js': 'application/javascript',
    '.png': 'image/png',
    '.jpg': 'image/jpeg',
    '.jpeg': 'image/jpeg',
    '.gif': 'image/gif',
    '.svg': 'image/svg+xml',
    '.ico': 'image/x-icon',
    '.txt': 'text/plain'
};

const server = http.createServer((req, res) => {
    // Parser l'URL
    let filePath = req.url === '/' ? '/index.html' : req.url;

    // Nettoyer le chemin
    filePath = '.' + filePath;

    // Obtenir l'extension du fichier
    const extname = path.extname(filePath).toLowerCase();
    const contentType = mimeTypes[extname] || 'application/octet-stream';

    // Lire le fichier
    fs.readFile(filePath, (err, content) => {
        if (err) {
            if (err.code === 'ENOENT') {
                // Fichier non trouv√©, retourner 404
                res.writeHead(404, { 'Content-Type': 'text/html' });
                res.end('<h1>404 - Page non trouv√©e</h1><p>Le fichier demand√© n\'existe pas.</p>');
            } else {
                // Erreur serveur
                res.writeHead(500);
                res.end(`Erreur serveur: ${err.code}`);
            }
        } else {
            // Succ√®s
            res.writeHead(200, { 
                'Content-Type': contentType,
                'Access-Control-Allow-Origin': '*',
                'Access-Control-Allow-Methods': 'GET, POST, PUT, DELETE, OPTIONS',
                'Access-Control-Allow-Headers': 'Content-Type'
            });
            res.end(content);
        }
    });
});

server.listen(PORT, HOST, () => {
    console.log(`üöÄ Serveur COMATRA FISH MARINE d√©marr√© sur http://${HOST}:${PORT}`);
    console.log(`üìÅ Serveur de fichiers statiques actif`);
    console.log(`üåê Site accessible √† l'adresse: http://localhost:${PORT}`);
});

// Gestion propre de l'arr√™t
process.on('SIGTERM', () => {
    console.log('üõë Arr√™t du serveur...');
    server.close(() => {
        console.log('‚úÖ Serveur arr√™t√© proprement');
        process.exit(0);
    });
});

process.on('SIGINT', () => {
    console.log('üõë Arr√™t du serveur...');
    server.close(() => {
        console.log('‚úÖ Serveur arr√™t√© proprement');
        process.exit(0);
    });
});

styles.css.content:
/* Animations keyframes */
@keyframes pulse-glow {
    0%,
    100% {
        box-shadow: 0 12px 30px rgba(3, 150, 199, 0.4);
    }
    50% {
        box-shadow: 0 12px 40px rgba(3, 150, 199, 0.7);
    }
}

@keyframes shimmer {
    0% {
        background-position: -200% 0;
    }
    100% {
        background-position: 200% 0;
    }
}

@keyframes bounce-rotate {
    0%,
    100% {
        transform: translateY(-5px) scale(1.15) rotate(10deg);
    }
    50% {
        transform: translateY(-8px) scale(1.18) rotate(15deg);
    }
}

@keyframes social-float {
    0%,
    100% {
        transform: translateY(-6px) scale(1.2) rotate(-8deg);
    }
    50% {
        transform: translateY(-10px) scale(1.25) rotate(-12deg);
    }
}

/* Variables CSS pour les couleurs COMATRA */
:root {
    --cfm-dark-blue: #01356d;
    --cfm-medium-blue: #016ca5;
    --cfm-bright-blue: #0396c7;
    --cfm-cyan-blue: #04bbdf;
    --cfm-light-cyan: #90e0ef;
    --cfm-very-light-cyan: #beedf4;

    --white: #ffffff;
    --light-gray: #f8f9fa;
    --gray: #6c757d;
    --dark-gray: #343a40;

    --shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
    --shadow-lg: 0 10px 25px rgba(0, 0, 0, 0.15);

    --border-radius: 8px;
    --border-radius-lg: 16px;

    --font-title: 'Montserrat', sans-serif;
    --font-body: 'Roboto', sans-serif;
}

/* Reset et base */
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

html {
    scroll-behavior: smooth;
}

body {
    font-family: var(--font-body);
    line-height: 1.6;
    color: var(--dark-gray);
}

h1,
h2,
h3,
h4,
h5,
h6 {
    font-family: var(--font-title);
    font-weight: 600;
    line-height: 1.2;
}

.container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 20px;
}

/* Navigation */
.navbar {
    background: transparent;
    position: fixed;
    top: 0;
    width: 100%;
    z-index: 1000;
    transition: background-color 0.3s ease;
}

.navbar.scrolled {
    background: rgba(255, 255, 255, 0.95);
    backdrop-filter: blur(10px);
    box-shadow: var(--shadow);
}

.nav-container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 20px;
    display: flex;
    justify-content: space-between;
    align-items: center;
    height: 80px;
}

.nav-logo {
    display: flex;
    align-items: center;
    gap: 10px;
}

.logo-image {
    height: 50px;
    width: auto;
    object-fit: contain;
    transition: filter 0.3s ease;
}

.navbar.scrolled .logo-image {
    filter: none;
}

.logo-subtitle {
    font-family: var(--font-title);
    font-size: 14px;
    color: rgba(255, 255, 255, 0.8);
    transition: color 0.3s ease;
}

.navbar.scrolled .logo-subtitle {
    color: var(--gray);
}

.nav-menu {
    display: flex;
    gap: 30px;
}

.nav-link {
    font-family: var(--font-title);
    font-weight: 500;
    text-decoration: none;
    color: var(--white);
    transition: color 0.3s ease;
}

.nav-link:hover {
    color: var(--cfm-light-cyan);
}

.navbar.scrolled .nav-link {
    color: var(--dark-gray);
}

.navbar.scrolled .nav-link:hover {
    color: var(--cfm-bright-blue);
}

.nav-toggle {
    display: none;
    flex-direction: column;
    cursor: pointer;
}

.bar {
    width: 25px;
    height: 3px;
    background: var(--white);
    margin: 3px 0;
    transition: 0.3s;
}

/* Hero Section */
.hero {
    background: linear-gradient(rgba(1, 53, 109, 0.7), rgba(1, 108, 165, 0.6)),
        url('image/back-hero.jpg');
    background-size: cover;
    background-position: center;
    background-attachment: fixed;
    min-height: 100vh;
    display: flex;
    align-items: center;
    position: relative;
