/* Gallery Slider */
.gallery {
    padding: 40px 0;
    overflow: hidden;
    background: linear-gradient(rgba(1, 53, 109, 0.7), rgba(1, 108, 165, 0.6)),
        url('image/back-hero.jpg');
    background-size: cover;
    background-position: center;
    background-attachment: fixed;
}

.gallery h3 {
    font-size: 2rem;
    color: var(--white);
    text-align: center;
    margin-bottom: 40px;
}

.gallery-slider {
    position: relative;
    width: 100%;
    height: 600px;
    overflow: hidden;
    border-radius: var(--border-radius-lg);
    box-shadow: var(--shadow-lg);
}

.gallery-track {
    display: flex;
    flex-direction: column;
    gap: 15px;
    animation: slideShow 40s linear infinite;
    height: calc(200px * 30 + 15px * 29); /* 30 images + 29 gaps pour effet infini */
}

.gallery-track img {
    width: 100%;
    height: 200px;
    object-fit: cover;
    border-radius: var(--border-radius);
    box-shadow: var(--shadow);
    flex-shrink: 0;
    transition: transform 0.3s ease, box-shadow 0.3s ease;
}

.gallery-track img:hover {
    transform: scale(1.02);
    box-shadow: 0 15px 35px rgba(0, 0, 0, 0.3);
    z-index: 2;
    position: relative;
}

/* Animation du slider - D√©filement vertical infini */
@keyframes slideShow {
    0% {
        transform: translateY(0);
    }
    100% {
        transform: translateY(calc(-200px * 15 - 15px * 14)); /* D√©filement de 15 images + 14 gaps */
    }
}

/* Pause l'animation au survol */
.gallery-slider:hover .gallery-track {
    animation-play-state: paused;
}

/* Responsive pour mobile */
@media (max-width: 768px) {
    .gallery-track img {
        width: 100%;
        height: 170px;
    }

    .gallery-slider {
        height: 500px;
    }

    .gallery-track {
        animation: slideShowMobile 35s linear infinite;
        height: calc(170px * 30 + 15px * 29);
    }

    @keyframes slideShowMobile {
        0% {
            transform: translateY(0);
        }
        100% {
            transform: translateY(calc(-170px * 15 - 15px * 14));
        }
    }

    /* Centre les ic√¥nes des cartes sur mobile */
    .card-icon, .service-icon {
        margin-left: auto;
        margin-right: auto;
    }
}

@media (max-width: 480px) {
    .gallery-track img {
        width: 100%;
        height: 140px;
    }

    .gallery-slider {
        height: 400px;
    }

    .gallery-track {
        animation: slideShowSmall 30s linear infinite;
        height: calc(140px * 30 + 15px * 29);
    }

    @keyframes slideShowSmall {
        0% {
            transform: translateY(0);
        }
        100% {
            transform: translateY(calc(-140px * 15 - 15px * 14));
        }
    }

    .gallery h3 {
        font-size: 1.8rem;
        margin-bottom: 30px;
    }

    /* Centre les ic√¥nes des cartes sur mobile */
    .card-icon, .service-icon {
        margin-left: auto;
        margin-right: auto;
    }
}

