    color: var(--cfm-bright-blue);
    margin-top: 5px;
    flex-shrink: 0;
}

.map-section h3 {
    font-size: 1.8rem;
    color: var(--cfm-dark-blue);
    text-align: center;
    margin-bottom: 30px;
}

.map-container {
    background: var(--light-gray);
    border-radius: var(--border-radius-lg);
    padding: 40px;
}

.map-placeholder {
    height: 400px;
    background: var(--white);
    border-radius: var(--border-radius);
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    color: var(--gray);
    text-align: center;
}

.map-placeholder i {
    font-size: 64px;
    margin-bottom: 20px;
    color: var(--cfm-bright-blue);
}

/* Partners Section */
.partners {
    background: linear-gradient(rgba(1, 53, 109, 0.7), rgba(1, 108, 165, 0.6)),
        url('image/back-hero.jpg');
    background-size: cover;
    background-position: center;
    background-attachment: fixed;
}

.partners .section-header h2 {
    color: var(--white);
}

.partners .section-header p {
    color: rgba(255, 255, 255, 0.9);
}

.partners-section {
    background: var(--white);
    padding: 60px;
    border-radius: var(--border-radius-lg);
    margin-bottom: 60px;
}

.partners-section h3 {
    font-size: 1.8rem;
    color: var(--cfm-dark-blue);
    text-align: center;
    margin-bottom: 40px;
}

.partners-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(180px, 1fr));
    gap: 20px;
}

.partners-text {
    max-width: 900px;
    margin: 0 auto;
}

.partners-text p {
    font-size: 1.1rem;
    line-height: 1.8;
    color: var(--gray);
    text-align: center;
}

.partners-text strong {
    color: var(--cfm-dark-blue);
    font-weight: 600;
}

.partner-logo {
    background: var(--light-gray);
    padding: 20px;
    border-radius: var(--border-radius);
    text-align: center;
    font-size: 12px;
    font-weight: 500;
    color: var(--gray);
}

.testimonials {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(400px, 1fr));
    gap: 40px;
    margin-bottom: 60px;
}

.testimonial {
    background: var(--white);
    padding: 40px;
    border-radius: var(--border-radius-lg);
    box-shadow: var(--shadow);
}

.testimonial-header {
    display: flex;
    align-items: center;
    margin-bottom: 25px;
}

.testimonial-avatar {
    width: 50px;
    height: 50px;
    background: var(--cfm-bright-blue);
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    margin-right: 15px;
}

.testimonial-avatar i {
    color: var(--white);
    font-size: 20px;
}

.testimonial-info h4 {
    color: var(--cfm-dark-blue);
    margin-bottom: 5px;
}

.testimonial-info p {
    font-size: 14px;
    color: var(--gray);
}

.testimonial p {
    color: var(--gray);
    font-style: italic;
    line-height: 1.6;
}

.achievements {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
    gap: 30px;
}

.achievement {
    background: var(--white);
    padding: 30px;
    border-radius: var(--border-radius);
    box-shadow: var(--shadow);
    text-align: center;
}

.achievement-number {
    font-size: 2.5rem;
    font-weight: 700;
    color: var(--cfm-bright-blue);
    font-family: var(--font-title);
    margin-bottom: 10px;
}

.achievement-label {
    font-size: 14px;
    color: var(--gray);
}

/* Contact Section */
.contact {
    background: var(--white);
}

.contact-content {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 80px;
}

.contact-form {
    background: var(--light-gray);
    padding: 40px;
    border-radius: var(--border-radius-lg);
}

.contact-form h3 {
    font-size: 1.8rem;
    color: var(--cfm-dark-blue);
    margin-bottom: 30px;
}

.form-group {
    margin-bottom: 25px;
}

.form-group label {
    display: block;
    margin-bottom: 8px;
    font-weight: 500;
    color: var(--dark-gray);
}

.form-group input,
.form-group select,
.form-group textarea {
    width: 100%;
    padding: 12px 16px;
    border: 2px solid #e9ecef;
    border-radius: var(--border-radius);
    font-family: var(--font-body);
    font-size: 16px;
    transition: border-color 0.3s ease;
}

.form-group input:focus,
.form-group select:focus,
.form-group textarea:focus {
    outline: none;
    border-color: var(--cfm-bright-blue);
}

.form-group textarea {
    resize: vertical;
    min-height: 120px;
}

.error-message {
    color: #dc3545;
    font-size: 14px;
    margin-top: 5px;
    display: none;
}

.error-message.show {
    display: block;
}

/* Submit button styling */
.btn-submit {
    width: 100%;
    padding: 16px 32px;
    background: linear-gradient(135deg, var(--cfm-bright-blue), var(--cfm-medium-blue));
    color: var(--white);
    border: none;
    border-radius: var(--border-radius);
    font-size: 1.1rem;
    font-weight: 600;
    font-family: var(--font-title);
    cursor: pointer;
    transition: all 0.3s ease;
    box-shadow: 0 4px 15px rgba(3, 150, 199, 0.3);
    position: relative;
    overflow: hidden;
}

.btn-submit::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(255,255,255,0.2), transparent);
    transition: left 0.5s ease;
}

.btn-submit:hover {
    transform: translateY(-3px);
    box-shadow: 0 8px 25px rgba(3, 150, 199, 0.4);
    background: linear-gradient(135deg, var(--cfm-medium-blue), var(--cfm-dark-blue));
}

.btn-submit:hover::before {
    left: 100%;
}

.btn-submit:active {
    transform: translateY(-1px);
    box-shadow: 0 4px 15px rgba(3, 150, 199, 0.3);
}

.btn-submit.loading {
    opacity: 0.7;
    cursor: not-allowed;
}

.button-loading {
    display: none;
}

.btn-submit.loading .button-text {
    display: none;
}

.btn-submit.loading .button-loading {
    display: inline;
}

.contact-info h3 {
    font-size: 1.8rem;
    color: var(--cfm-dark-blue);
    margin-bottom: 30px;
}

.contact-item {
    display: flex;
    gap: 20px;
    margin-bottom: 35px;
}

.contact-icon {
    width: 50px;
    height: 50px;
    background: var(--cfm-bright-blue);
    border-radius: var(--border-radius);
    display: flex;
    align-items: center;
    justify-content: center;
    flex-shrink: 0;
}

.contact-icon i {
    color: var(--white);
    font-size: 20px;
}

.contact-details h4 {
    color: var(--cfm-dark-blue);
    margin-bottom: 8px;
}

.contact-details p {
    color: var(--gray);
    margin-bottom: 3px;
}

.social-media h3 {
    font-size: 1.3rem;
    color: var(--cfm-dark-blue);
    margin-bottom: 20px;
}

.social-links {
    display: flex;
    gap: 15px;
}

.social-links a {
    width: 50px;
    height: 50px;
    background: var(--cfm-bright-blue);
    border-radius: var(--border-radius);
    display: flex;
    align-items: center;
    justify-content: center;
    color: var(--white);
    text-decoration: none;
    transition: background-color 0.3s ease;
}

.social-links a:hover {
    background: var(--cfm-medium-blue);
    transform: translateY(-6px) scale(1.2) rotate(-8deg);
    box-shadow: 0 15px 35px rgba(1, 108, 165, 0.5);
    animation: social-float 2s ease-in-out infinite;
}

.social-links a:active {
    transform: translateY(-3px) scale(1.1);
}

.business-hours {
    margin-top: 40px;
}

.business-hours h3 {
    font-size: 1.3rem;
    color: var(--cfm-dark-blue);
    margin-bottom: 20px;
}

.hours-item {
    display: flex;
    justify-content: space-between;
    margin-bottom: 8px;
    color: var(--gray);
}

/* Footer */
.footer {
    background: var(--cfm-dark-blue);
    color: var(--white);
    padding: 60px 0 20px;
}

.footer-content {
    display: grid;
    grid-template-columns: 2fr 1fr 1fr;
    gap: 60px;
    margin-bottom: 40px;
}

.footer-logo {
    display: flex;
    align-items: center;
    gap: 15px;
    margin-bottom: 25px;
}

.footer-logo .logo-image {
    height: 40px;
    width: auto;
    object-fit: contain;
}

.footer-logo .logo-subtitle {
    font-size: 16px;
    color: var(--white);
}

.footer-section p {
    line-height: 1.7;
    margin-bottom: 30px;
    opacity: 0.8;
}

.footer-section h4 {
    font-size: 1.2rem;
    margin-bottom: 25px;
    color: var(--cfm-light-cyan);
}

.footer-section ul {
    list-style: none;
}

.footer-section ul li {
    margin-bottom: 10px;
}

.footer-section a {
    color: rgba(255, 255, 255, 0.8);
    text-decoration: none;
    transition: color 0.3s ease;
}

.footer-section a:hover {
    color: var(--cfm-light-cyan);
}

.footer-contact p {
    margin-bottom: 15px;
    font-size: 14px;
}

.footer-social {
    display: flex;
    gap: 15px;
    margin-top: 25px;
}

.footer-social a {
    width: 40px;
    height: 40px;
    background: var(--cfm-bright-blue);
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    text-decoration: none;
    transition: background-color 0.3s ease;
}

.footer-social a:hover {
    background: var(--cfm-light-cyan);
    transform: translateY(-5px) scale(1.15) rotate(10deg);
    box-shadow: 0 10px 25px rgba(4, 187, 223, 0.5);
    animation: bounce-rotate 1.5s infinite;
}

.footer-social a:active {
    transform: translateY(-2px) scale(1.08);
}

.copyright {
    font-size: 14px;
    opacity: 0.6;
    margin-top: 20px;
}

/* Success message */
.success-message {
    background: #d4edda;
    color: #155724;
    padding: 15px;
    border-radius: var(--border-radius);
    margin-bottom: 20px;
    display: none;
}

.success-message.show {
    display: block;
}

/* Loading state */
.button-loading {
    display: none;
}

.loading .button-text {
    display: none;
}

.loading .button-loading {
    display: inline;
}

/* Responsive Design */
@media (max-width: 768px) {
    .nav-menu {
        position: fixed;
        left: -100%;
        top: 80px;
        flex-direction: column;
        background-color: var(--white);
        width: 100%;
        text-align: center;
        transition: 0.3s;
        box-shadow: var(--shadow);
        padding: 20px 0;
    }

    .nav-menu.active {
        left: 0;
    }

    .nav-menu a {
        padding: 15px;
        display: block;
        text-decoration: none;
        color: var(--dark-gray);
        font-family: var(--font-title);
        font-weight: 500;
        transition: color 0.3s ease;
    }

    .nav-menu a:hover {
        color: var(--cfm-bright-blue);
    }

    .nav-menu a.active {
        color: var(--cfm-bright-blue);
        font-weight: 600;
    }

    .nav-toggle {
        display: flex;
    }

    .nav-toggle.active .bar:nth-child(2) {
        opacity: 0;
    }

    .nav-toggle.active .bar:nth-child(1) {
        transform: translateY(8px) rotate(45deg);
    }

    .nav-toggle.active .bar:nth-child(3) {
        transform: translateY(-8px) rotate(-45deg);
    }

    .hero-content {
        grid-template-columns: 1fr;
        gap: 40px;
        text-align: center;
    }

    .hero-text h1 {
        font-size: 2.5rem;
    }

    .hero-text h2 {
        font-size: 1.4rem;
    }

    .hero-buttons {
        flex-direction: column;
        gap: 15px;
    }

    .agency-locations {
        justify-content: center;
        flex-wrap: wrap;
        gap: 20px;
    }

    .section-header h2 {
        font-size: 2.2rem;
    }

    .about-content {
        grid-template-columns: 1fr;
        gap: 40px;
    }

    .contact-content {
        grid-template-columns: 1fr;
        gap: 40px;
    }

    .footer-content {
        grid-template-columns: 1fr;
        gap: 40px;
    }

    .stats {
        grid-template-columns: 1fr;
    }

    .expertise-cards {
        grid-template-columns: 1fr;
    }

    .product-categories {
        grid-template-columns: 1fr;
    }

    .services-grid {
        grid-template-columns: 1fr;
        gap: 30px;
    }

    .agencies-grid {
        grid-template-columns: 1fr;
    }

    .headquarters-wrapper .agency-card {
        max-width: 100%;
    }

    .departments-title h3 {
        font-size: 1.6rem;
        letter-spacing: 1px;
    }

    .testimonials {
        grid-template-columns: 1fr;
    }

    .achievements {
        grid-template-columns: repeat(2, 1fr);
    }
}

@media (max-width: 480px) {
    .container {
        padding: 0 15px;
    }

    .nav-container {
        padding: 0 15px;
    }

    .hero-text h1 {
        font-size: 2rem;
    }

    .hero-text h2 {
        font-size: 1.2rem;
    }

    .section-header h2 {
        font-size: 1.8rem;
    }

    .about-text h3 {
        font-size: 1.5rem;
    }

    .card {
        padding: 25px;
    }

    .product-card {
        padding: 20px;
    }

    .contact-form {
        padding: 25px;
    }

    .departments-title h3 {
        font-size: 1.3rem;
    }

    .achievements {
        grid-template-columns: 1fr;
    }
}
